import 'package:flutter/material.dart';
import '/screens/pantalla_carrito.dart';
import '/screens/pantalla_inicio.dart';

// Un AppBar reutilizable para mantener la consistencia en la app.
class AppBarComun extends StatelessWidget implements PreferredSizeWidget {
  const AppBarComun({super.key});

  @override
  Widget build(BuildContext context) {
    return AppBar(
      // El título se alinea a la izquierda por defecto.
      title: const Text('Mi Tienda'),
      actions: [
        // Icono para navegar a la pantalla de inicio.
        IconButton(
          icon: const Icon(Icons.home),
          onPressed: () {
            // Evita añadir rutas duplicadas si ya estamos en el inicio.
            if (ModalRoute.of(context)?.settings.name != '/') {
              Navigator.of(context).pushReplacement(
                MaterialPageRoute(builder: (context) => const PantallaInicio()),
              );
            }
          },
        ),
        // Icono del carrito de compras con un contador.
        IconButton(
          icon: Stack(
            children: [
              const Icon(Icons.shopping_cart),
              Positioned(
                right: 0,
                child: Container(
                  padding: const EdgeInsets.all(1),
                  decoration: BoxDecoration(color: Colors.red, borderRadius: BorderRadius.circular(10)),
                  constraints: const BoxConstraints(minWidth: 16, minHeight: 16),
                  child: const Text('0', style: TextStyle(color: Colors.white, fontSize: 10), textAlign: TextAlign.center),
                ),
              ),
            ],
          ),
          onPressed: () => Navigator.of(context).push(MaterialPageRoute(builder: (context) => const PantallaCarrito())),
        ),
      ],
    );
  }

  @override
  Size get preferredSize => const Size.fromHeight(kToolbarHeight);
}
